import styled from 'styled-components';

export const Banner = styled.div`
    .banner {
        display: flex;
        flex-direction: column;
        z-index: 0;
        height: auto;

        align-items: center;
        justify-content: center;
        text-align: center;
    }

    .banner-image {
        width: 100%;
    }

    .banner-text {
        position: absolute;
        font-family: 'Montserrat', sans-serif;
        font-size: 100px;
        font-weight: 600;
        margin: 0px;
    }

    @media (min-width: 1224px) {

        .banner {
            text-align: justify;
        }

    }
`;

export default Banner;
