import styled, { css } from 'styled-components';

export const LegalSection = styled.section`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    width: 80vw;
    line-height: 20px;

    ${props => props.bg === 'dark' && css`
        background-color: #202020;  
    `}


`;

export default LegalSection;

