import styled from 'styled-components';

export const FeatureSection = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    background-color: #0d2233;

    p {
        font-size: 20px;
        width: 80vw;
    }



`

export const FeatureArray = styled.ul`
    display: flex;
    justify-content: center;
`


export const FeatureCard = styled.li`
    display: flex;
    flex-direction: column;
    align-items: center;

    width: 25vw;
    height: 40vh;
    margin: 30px;

    background-color: #4e5154;
    border-radius: 10px;

    p {
        font-size: 20px;

    }

    img {
        width: 10vw;

    }

`
