import React, { Component } from 'react';
// import { withCookies } from 'react-cookie';
// import { getSingleProduct } from '../../api';
// const [cookies, setCookie, removeCookie] = useCookies(['cookie-name']);

const Single = (props) => {
    console.log(props);


    if (props.product) {
        return (
            <main className='single-page'>
                <article className='single-article'>
                    <h1 className='product-header'>{props.productInfo.name}</h1>

                    <img alt={props.productInfo.img} className='single-image' 
                    src={`https://s3-us-west-1.amazonaws.com/hyfxi.ai-images/${this.state.productInfo.img}`}></img>

                    <h2>price: $ {props.productInfo.price}</h2>
                    <h3>{props.productInfo.description} Hello!</h3>

                </article>
                <button className='button add-button' onClick={() => this.addToCart()}>Add To Cart</button>
            </main>
        );
    }

    return null;

}

export default Single;

