import React from 'react'
import { useEffect, useState } from 'react'
import { useCookies } from 'react-cookie'
import {
    CardElement,
    Elements,
    useElements,
    useStripe
} from '@stripe/react-stripe-js'
import { getProductsInCart, createPaymentIntent } from '../../api'
import { useHistory } from 'react-router'

const CARD_ELEMENT_OPTIONS = {
    style: {
        base: {
            color: '#32325d',
            fontFamily: '"Helvetica Neue" , Helvetica, sans-serif',
            fontSmoothing: 'antialiased',
            fontSize: '40px',
            '::placeholder': {
                color: '#aab7c4'
            }
        },
        invalid: {
            color: '#fa755a',
            iconColor: '#fa755a'
        }
    }
}

const CheckoutForm = () => {
    const stripe = useStripe()
    const elements = useElements()
    const history = useHistory()

    const [error, setError] = useState(null)
    const [clientSecret, setClientSecret] = useState(null)
    const [cookies, removeCookie] = useCookies()

    const [metadata, setMetadata] = useState(null)
    const [succeeded, setSucceeded] = useState(false)
    const [processing, setProcessing] = useState(false)

    const [recipient, setRecipient] = useState(null)
    const [street, setStreet] = useState(null)
    const [city, setCity] = useState(null)
    const [theState, setTheState] = useState(null)
    const [code, setCode] = useState(null)
    const [country, setCountry] = useState(null)

    useEffect(() => {
        const productList = cookies.userCart
        console.log(productList)

        getProductsInCart(productList).then((products) => {
            createPaymentIntent(products)
                .then((clientSecret) => {
                    setClientSecret(clientSecret)
                })
                .catch((err) => {
                    setError(err.message)
                })
        })
    }, [cookies.userCart])

    const handlePaymentChange = (ev) => {
        if (ev.error) {
            setError(ev.error.message)
        } else {
            setError(null)
        }
    }

    const handleSubmit = async (ev) => {
        ev.preventDefault()
        setProcessing(true)

        const payload = await stripe.confirmCardPayment(clientSecret, {
            payment_method: {
                card: elements.getElement(CardElement),
                billing_details: {
                    name: ev.target.name.value
                }
            },
            shipping: {
                name: recipient,
                address: {
                    line1: street,
                    city: city,
                    state: theState,
                    country: country,
                    postal_code: code
                }
            }
        })

        if (payload.error) {
            setError(`Payment failed: ${payload.error.message}`)
            setProcessing(false)
            console.log('[error]', payload.error)
        } else {
            setError(null)
            setSucceeded(true)
            setProcessing(false)
            setMetadata(payload.paymentIntent)
            history.push({ pathname: '/store/success' })
            removeCookie('userCart', { path: '/' })
            console.log('[PaymentIntent]', payload.paymentIntent)
        }
    }

    return (
        <main className="checkout-page">
            <form className="checkout-form" onSubmit={handleSubmit}>
                <div className="form-section">
                    <h2>Shipping</h2>

                    <label>Recipient Name</label>
                    <input
                        value={recipient}
                        onChange={(e) => setRecipient(e.target.value)}
                    />

                    <label>Street Address</label>
                    <input
                        value={street}
                        onChange={(e) => setStreet(e.target.value)}
                    />

                    <label>City</label>
                    <input
                        value={city}
                        onChange={(e) => setCity(e.target.value)}
                    />

                    <div className="double-input">
                        <label>State</label>
                        <input
                            value={theState}
                            onChange={(e) => setTheState(e.target.value)}
                        />

                        <label>Zip/Postal Code</label>
                        <input
                            value={code}
                            onChange={(e) => setCode(e.target.value)}
                        />
                    </div>

                    <label>Country</label>
                    <input
                        value={country}
                        onChange={(e) => setCountry(e.target.value)}
                    />
                </div>

                <div className="form-section">
                    <h2>Payment</h2>
                    <label htmlFor="card-element">Credit or Debit Card</label>
                    <CardElement
                        id="card-element"
                        options={CARD_ELEMENT_OPTIONS}
                        onChange={handlePaymentChange}
                    />
                    <div className="card-errors" role="alert">
                        {error}
                    </div>
                </div>

                <button className="button checkout-button" type="submit">
                    Submit Payment
                </button>
            </form>
        </main>
    )
}

const CheckoutPage = (props) => {
    console.log(props)

    return (
        <Elements stripe={props.stripe}>
            <CheckoutForm />
        </Elements>
    )
}

export default CheckoutPage

