import React from 'react';
import { useEffect, useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { useCookies } from 'react-cookie';
import {
    CardElement,
    Elements,
    useElements,
    useStripe
} from '@stripe/react-stripe-js';
import { getProductsInCart, createPaymentIntent} from '../../../api';

const CARD_ELEMENT_OPTIONS = {
    style: {
        base: {
            color: '#32325d',
            fontFamily: '"Helvetica Neue" , Helvetica, sans-serif',
            fontSmoothing: 'antialiased',
            fontSize: '40px',
            '::placeholder': {
                color: '#aab7c4'
            }
        },
        invalid: {
            color: '#fa755a',
            iconColor: '#fa755a'
        }
    }
};

const CheckoutForm = () => {
    const stripe = useStripe();
    const elements = useElements();

    const [error, setError] = useState(null);
    const [clientSecret, setClientSecret] = useState(null);
    const [cookies] = useCookies();

    const [metadata, setMetadata] = useState(null);
    const [succeeded, setSucceeded] = useState(false);
    const [processing, setProcessing] = useState(false);

    const [recipient, setRecipient] = useState(null);
    const [street, setStreet] = useState(null);
    const [city, setCity] = useState(null);
    const [state, setState] = useState(null);

    useEffect(() => {
        const productList = cookies.userCart;
        getProductsInCart(productList).then((products) => { 

            createPaymentIntent(products)
            .then((clientSecret) => {
                console.log('Payment intent success!')
                setClientSecret(clientSecret);
            })
            .catch((err) => {
                setError(err.message);
            });

        });

    }, []); //keeps hook to only once, intresting?

    const handlePaymentChange = (ev) => {
        if (ev.error) {
            setError(ev.error.message);
        } else {
            setError(null);
        }
    }

    const handleShippingChange = (ev) => {

    }

    const handleSubmit = async (ev) => {
        ev.preventDefault();
        setProcessing(true);
        console.log('EVENT', ev);


        const payload = await stripe.confirmCardPayment(clientSecret, {
            payment_method: {
                card: elements.getElement(CardElement),
                billing_details: {
                    name: ev.target.name.value,
                },
            },
        });

        if (payload.error) {
            setError(`Payment failed: ${payload.error.message}`);
            setProcessing(false);
            console.log("[error]", payload.error);
        } else {
            setError(null);
            setSucceeded(true);
            setProcessing(false);
            setMetadata(payload.paymentIntent);
            console.log("[PaymentIntent]", payload.paymentIntent);
        }

    };

    return (
        <main className='checkout-page'>
            <form className='checkout-form' onSubmit={handleSubmit}>
                <div className='form-section'>
                    <h2>Shipping</h2>

                    <label>Recipient Name</label>
                    <input value='recipient' />

                    <label>Street Adress</label>
                    <input value='street' />

                    <label>City</label>
                    <input value='city' />

                    <div className='double-input'>
                        <label>State</label>
                        <input value='state' />

                        <label>Zip/Postal Code</label>
                        <input value='code' />
                    </div>


                    <label>Country</label>
                    <input value='country' />
                </div>

                <div className='form-section'>
                    <h2>Payment</h2>
                    <label htmlFor='card-element'>Credit or Debit Card</label>
                    <CardElement 
                        id='card-element' 
                        options={CARD_ELEMENT_OPTIONS} 
                        onChange={handlePaymentChange}/>
                    <div className='card-errors' role='alert'>{error}</div>
                </div>

                <button className='button checkout-button' type='submit'>Submit Payment</button>

            </form>
        </main>

        );
}

const stripePromise = loadStripe('pk_test_51I0CsSIzd0xoN2NEasujFfk8OIEYrwO5KrdT4fYAjUiQ8iJVP1fumZ3XGDf1XJq0Q3C9I0wlsQxbtYY2xRAvP2eZ0024wl5yqd');
const CheckoutPage = () => {
        return (
          <Elements stripe={stripePromise}>
            <CheckoutForm />
        </Elements>
    );
}

export default CheckoutPage; 


