import styled from 'styled-components';

export const FeatureSection = styled.div`
    display: flex;
    flex-direction: column;

    background-color: #0d2233;
    padding-top: 200px;
    padding-bottom: 200px;
    width: 100vw;

    p {
        font-size: 20px;
        width: 80vw;
        line-height: 30px;
    }

    ul {
        display: flex;
        justify-content: center;
        margin-top: 20px;
        margin-bottom: 20px;

        border: 5px solid red;
        width: 100vw;


    }

`



export const FeatureCard = styled.li`
    display: flex;
    flex-direction: column;
    align-items: center;
    align-content: center;

    width: 800px;
    height: 30vw;
    box-shadow: 10px 10px 10px 5px rgb(7,19,30,0.7);

    background-color: #4e5154;
    border-radius: 20px;

    p {
        font-family: 'Montserrat', sans-serif;
        font-weight: 600;
        color: white;

        width: initial;
        font-size: 25px;
        margin-top: 30px;
        margin-left: auto;
        margin-right: auto;

    }

    img {
        width: 10vw;
        position: absolute;
        margin-top: 135px;
    }


`
