import styled from 'styled-components';

export const FeatureSection = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    background-color: #0d2233;

    p {
        font-size: 20px;
        width: 80vw;
    }

    ul {
        display: flex;
        justify-content: center;
        margin-top: 20px;
        margin-bottom: 20px;

    }

`



export const FeatureCard = styled.li`
    display: flex;
    flex-direction: column;
    align-items: center;

    width: 25vw;
    height: 30vw;
    margin: 30px;

    background-color: #4e5154;
    border-radius: 10px;

    p {
        width: 10vw;
        font-size: 25px;
        margin-top: 30px;
    }

    img {
        width: 10vw;
        position: absolute;
        margin-top: 135px;
    }


`
