import styled from 'styled-components';

const PageWrapper = styled.main`
    height: auto;
    padding-bottom: 40px;

    h1 {
        margin-bottom: 0px;
        color: black;
        font-family: 'Roboto Condensed', sans-serif;
        font-size: 50px;
        margin-top: 30px;
        margin-bottom: 20px;
        text-decoration: none;
        font-weight: 700;
    }

    img {
        width: 90%;
        height: 40%;
        object-fit: contain;
    }
`;

export default SingleCard;
