import styled from 'styled-components';

export const FeatureArray = styled.ul`
    display: flex;
    justify-content: center;
`


export const FeatureCard = styled.ul`
    display: flex;
    flex-direction: column;
    align-items: center;

    width: 25vw;
    height: 40vh;

    background-color: #4e5154;
    border-radius: 10px;

    p {
        font-size: 20px;

    }

    img {
        width: 10vw;

    }

`
