import styled from 'styled-components';

export const GlobeBanner = styled.div`
    display: flex;
    flex-direction: column;
    z-index: 0;
    height: auto;

    justify-content: center;
    align-items: center;
    text-align: center;
    background-color: yellow;
    mask-image: linear-gradient(rgba(0, 0, 0, 1.0), rgba(2, 21, 32, 0.0));

    /*
