    -webkit-mask-image: linear-gradient(to top, transparent 5%, black 100%);
    mask-image: linear-gradient(to top, transparent 5%, black 100%); 

    #021527
    #011d31
    #00273e

    */


    div {
        height: 100px;

    }

    img {
        width: 100%;
        mask-image: linear-gradient(rgba(0, 0, 0, 1.0), yellow);
        z-index: 2;

    }

    h1 {
        position: absolute;
        font-family: 'Montserrat', sans-serif;
        font-size: 100px;
        font-weight: 600;
        top: 250px;
    }

    h2 {
        font-size: 30px;
        font-family: 'Lora';
        font-style: italic;
        color: white;

        width: 50vw;
        position: absolute;
        margin-top: 130px;
        line-height: 40px;

    }

    @media (min-width: 1224px) {
        h1 {
            top: 150px;
        }

        h2 {
            font-size: 40px;
            width: 60vw;
            line-height: 70px;
            margin-top: 450px;
        }

    }
`;

export default GlobeBanner;
