import styled from 'styled-components';

export const FeatureArray = styled.ul`
    display: flex;
    justify-content: center;


    li {
        display: flex;
        justify-content: center;

        width: 25vw;
        height: 30vh;
        background-color: #4e5154;
        margin: 30px;
        border-radius: 10px;

    }

`

export default FeatureArray;
